  return((3*t^2 - 9*t - 5,t^2 - 3*t + 2));
}

path trail = parm(0);

for (t = 0; t <= 4; t = t + 0.1) {
  trail = trail--parm(t);
}

trail = trail--parm(4);

draw(trail,red);

